import React from "react";
import ReactAnimatedWeather from "react-animated-weather";

const codeMapping = {
  "01d": "CLEAR_DAY",
  "01n": "CLEAR_NIGHT",
  "02d": "PARTLY_CLOUDY_DAY",
  "02n": "PARTLY_CLOUDY_NIGHT",
  "03d": "PARTLY_CLOUDY_DAY",
  "03n": "PARTLY_CLOUDY_NIGHT",
  "04d": "CLOUDY",
  "04n": "CLOUDY",
  "09d": "RAIN",
  "09n": "RAIN",
  "10d": "RAIN",
  "10n": "RAIN",
  "11d": "RAIN",
  "11n": "RAIN",
  "13d": "SNOW",
  "13n": "SNOW",
  "50d": "FOG",
  "50n": "FOG",
};

const descriptionMapping = {
  "clear-sky-day": "01d",
  "clear-sky-night": "01n",
  "few-clouds-day": "02d",
  "few-clouds-night": "02n",
  "scattered-clouds-day": "03d",
  "scattered-clouds-night": "03n",
  "broken-clouds-day": "04d",
  "broken-clouds-night": "04n",
  "shower-rain-day": "09d",
  "shower-rain-night": "09n",
  "rain-day": "10d",
  "rain-night": "10n",
  "thunderstorm-day": "11d",
  "thunderstorm-night": "11n",
  "snow-day": "13d",
  "snow-night": "13n",
  "mist-day": "50d",
  "mist-night": "50n",
};

export default function WeatherIcon(props) {
  const iconCode = descriptionMapping[props.code] || props.code;
  const icon = codeMapping[iconCode];

  if (!icon) {
    console.error(`No icon mapping found for code: ${props.code}`);
    return null;
  }

  return (
    <ReactAnimatedWeather
      icon={icon}
      color="#1e1e1e"
      size={props.size}
      animate={true}
    />
  );
}
